import React, { useState } from 'react';
import {
  apiTweetCreate
} from './lookup';
import {ActionBtn } from './buttons'


export function TweetComponent(props) {
  const textAreaRef = React.createRef();
  const [newTweets, setNewTweets] = useState([]);

  const canTweet = props.canTweet === "false" ? false : true;

  const handleSubmit = (event) => {
    event.preventDefault();
    const newVal = textAreaRef.current.value;


    const handleBackendUpdate = (response, status) => {
      let tempNewTweets = [...newTweets];
      if (status === 201) {
        tempNewTweets.unshift(response);
        setNewTweets(tempNewTweets);
      }
      else {
        console.log(response, status)
        alert('An error ocurred,please try again!');
      }
    }

    apiTweetCreate(newVal, handleBackendUpdate);
    textAreaRef.current.value = '';
  }
  return <div div className = { props.className } >
    {canTweet === true && <div className='col-12 mb-3'>
      <form onSubmit={handleSubmit}>
        <textarea ref={textAreaRef} required={true} name='tweet' className='form-control'></textarea>
        <button type='submit' className='btn btn-primary my-3'>Tweet</button>
      </form>
    </div>}
    <TweetLists newTweets={newTweets} {...props} />
  </div>
};  

export function ParentTweet(props) {
  const { tweet } = props
  return tweet.parent ?
    <div className='row'>
      <div className='col-11 mx-auto p-3 border rounded'>
        <p className='mb-0 text-muted small'>Retweet</p>
        <Tweet hideAction className={''} tweet={tweet.parent} />
    </div>
    </div> :
    null
}
  
  
 export function Tweet(props) {
   const { tweet, didRetweet, hideAction } = props;
   const [actionTweet, setActionTweet] = useState(props.tweet ? props.tweet : null);
   const className = props.className ? props.className : 'col-10 mx-auto col-md-6';

   const handlePerformAction = (newActionTweet, status) => {
     if (status === 200) {
       setActionTweet(newActionTweet);
     }
     else if (status === 201) {
       if (didRetweet) {
         didRetweet(newActionTweet);
       }
     }
  }
   
    return <div className={className}>
      <p>
        {tweet.id} - {tweet.content}
      </p>
      <ParentTweet tweet={tweet} />
      {(actionTweet &&  hideAction !==true) && <div className='btn btn-group'>
        <ActionBtn tweet={actionTweet} didPerformAction={handlePerformAction } action={{ type: "like", display: "like" }} />
        <ActionBtn tweet={actionTweet} didPerformAction={handlePerformAction } action={{ type: "unlike", display: "unlike" }} />
        <ActionBtn tweet={actionTweet} didPerformAction={handlePerformAction } action={{ type: "retweet", display: "retweet" }} />
      </div>
      }
    </div>
  }
